import React, { useState } from "react";

export const InputElement = () => {
  const [inputText, setInputText] = useState("");
  return (
    <>
      <input
        value={inputText}
        onChange={(event) => setInputText(event.target.value)}
        placeholder="Enter Some Text"
      />
      {inputText}
    </>
  );
};

export default InputElement;
